import axios from "axios";
import _ from "lodash";
import { useState } from "react";
import { Link } from "react-router-dom";

function Search() {
  const [inputSearch, setinputSearch] = useState("");
  const [results, setResults] = useState([]);



  const clearSearch = () => {
    setinputSearch("");
    setResults([]);
  };
  const search = (value) => {
    setinputSearch(value);
    searchFromYouTube(value);
  };
  const searchFromYouTube = _.debounce((value) => {
    axios
      .get(
        `https://www.googleapis.com/youtube/v3/search?part=snippet&maxResults=10&q=${value}&key=AIzaSyAfMOsMbcN-coXJABWbYRwK5lf_lJDHNRc`
      )
      .then((res) => {
        setResults(res.data.items);
      });
  }, 500);

  return (
    <div >
    <div style={{display:'flex',flexDirection:"row",justifyContent:'center',paddingTop:80}}>
      <input
       style={{wid}}

        value={inputSearch}
        onChange={(e) => search(e.target.value)}
        type="text"
        placeholder="Search"
      />
      </div>

      {(inputSearch!="" )? (
        <ul>
          {results.map((item) => (
            <li style={{listStyleType:'none',border:'0.2px solid black',borderRadius:5,padding:5}}>
              <Link
                onClick={() => clearSearch()}
                to={`/video/${item.id.videoId}`}
              >
                <img src={item.snippet.thumbnails.high.url} width="50px" />{" "}
                {item.snippet.title}
              </Link>
            </li>
          ))}
        </ul>
      ) : (
        ""
      )}
    </div>
  );
}
export default Search;
