import "../App.css";
import axios from "axios";
import { useEffect, useState } from "react";
import { useParams } from "react-router";
import { useDispatch, useSelector } from "react-redux";
import { add, remove } from "../reducer/watchlater";

export default function MyVideo() {
  const dispatch = useDispatch();
  const [video, setVideo] = useState({});
  const [clicked, setClicked] = useState(false);
  const { id } = useParams();

  const state = useSelector((state)=>{
    return {watchlist:state.watchLater.watchLater}
  })

  useEffect(() => {
    axios
      .get(
        `https://youtube.googleapis.com/youtube/v3/videos?part=snippet&id=${id}&key=AIzaSyAYCdcNYe6mYsHOj5oUH1TeJUY7NWa84EM`
      )
      .then((res) => {
        setVideo(res.data.items[0]);
      });
  }, []);

  const addTo = () => {

    dispatch(add(video));
    console.log("clicked add")
    console.log(state.watchLate)
    setClicked(true);
  };
  const removeFrom = () => {
    dispatch(remove(id));
    setClicked(false);
  };

  return (
    <div className="continer">
      <h1>{video?.snippet?.title}</h1>
      <iframe
        width="900"
        height="600"
        src={`https://www.youtube.com/embed/${id}`}
        title="YouTube video player"
        frameborder="0"
        allow="accelerometer; 
         autoplay; clipboard-write; encrypted-media;
          gyroscope; picture-in-picture"
        allowfullscreen
      />

      {!clicked ? (
        <button onClick={() => addTo()}>Watch Later</button>
      ) : (
        <button onClick={() => removeFrom()}>Remove from watch</button>
      )}
    </div>
  );
}
