///REDUCER  /////////////////////////
const initState={
    yout:[
        {
name:''
    }],
    count:0
}

  const YoutReducer =(state=initState,{type,payload})=>{


    switch(type){

        case 'WATCH_LATER':
            return{
                ...state,
                yout:payload,
               

            }

            default:
                return state
    }
    
}

export default YoutReducer;
//ACTION   /////////////////////////////////////////////////////////
export const watchLater=(yout)=>{
    return{
        type:'WATCH_LATER',
        payload:yout
    }


}
