///REDUCER  /////////////////////////
const initState={
    yout:[{
        name:'Nora'
    },
    {
        name:'sara'
    },
    {
        name:'mohammad'
    }
],
    count:0
}

  const YoutReducer =(state=initState,{type,payload})=>{


    switch(type){

        case 'WATCH_LATER':
            return{
                ...state,
                yout:[...state.yout,],
                count:state.count + payload   
            }

            default:
                return state
    }
    
}

export default YoutReducer;


//ACTION   /////////////////////////////////////////////////////////
export const watchLater=(data)=>{
    return{
        type:'WATCH_LATER',
        payload:data
    }


}
