import React, { useEffect, useState } from 'react';
import axios from 'axios';

export default function Search() {

    //useState
    const [addNew, setAddNew]=useState([])


     const [search,setSearch]=useState([
         {
             name:'shahad'
         },
         {
             name:'sara'
         },
         {
           name:"shahad"  
         }
     ])
     
    useEffect(()=>{

//         axios.get('https://www.googleapis.com/youtube/v3/search?part=snippet&maxResults=20&q=hello&key=AIzaSyCkO63ZZdKMSYqlEl65LwEwy7qknwv-90g')
axios.get('https://www.googleapis.com/youtube/v3/search?part=snippet&maxResults=20&chart=mostPopular&key=AIzaSyA9PH9fvQgSQN3C_1zFQtDJc4joW0iHti0')

.then((res)=>{
    console.log(res)
    setAddNew(res.data.items)
 })

   },[])


    return ( 
        <>
       <AddNewForm setAddNew={setAddNew}/> 
<hr/>




<div className='BigBox'>

{search.fi(elm => elm.name === "shahad")
.map(FilName=>(
    <div className='SmallBox'>
    <li>{FilName.name}</li>
    </div>

    ))}
</div>

 {/* <div className='BigBox'>

{search.filter(elm => elm.name === "shahad")
.map(FilName=>(
    <div className='SmallBox'>
    <li>{FilName.name}</li>
    </div>

    ))}
</div> */}

        



 
       {/* {addNew.map((elm)=>(

           <h3>{elm.name}</h3>
       ))} */}
 
 
        </>
     );
}

 //Functions 


function AddNewForm(props) {

    const[name,setName]=useState()

    const handelSubmit=(event)=>{
        event.preventDefault();
        
        props.setAddNew(props =>props.concat({name:name}))
        
        alert(`${name}`)

            }


    return ( 
    <>

<h1 className='subject'>Youtube website</h1>
            
            
            <form className='searchForm' onSubmit={handelSubmit}>  

   <input className='searchInput'
    type='search'
    value={name}
    placeholder ='Search'
    onChange={e => {setName(e.target.value)}}></input>


   <input className='searchBtn' type='submit'></input>
            
            </form> 
    
    </> );
}
