import React, { useEffect, useState } from 'react';

export default function Search() {

    //useState
    const [addNew, setAddNew]=useState([])


     const [search,setSearch]=useState([
         {
             name:'shahad'
         },
         {
             name:'sara'
         },
         {
           name:"shahad"  
         }
     ])
     
     useEffect(()=>{

        axios.get('https://www.googleapis.com/youtube/v3/search?part=snippet&maxResults=20&q=hello&key=AIzaSyCkO63ZZdKMSYqlEl65LwEwy7qknwv-90g')
.then

     },[])


    return ( 
        <>
        



{search.filter(elm => elm.name === "shahad")
.map(FilName=>(<li>{FilName.name}</li>))}



        



{/* <AddNewForm setAddNew={setAddNew}/> */}
       {/* {addNew.map((elm)=>(

           <h3>{elm.name}</h3>
       ))} */}
 
 
        </>
     );
}

 //Functions 


function AddNewForm(props) {

    const[name,setName]=useState()

    const handelSubmit=(event)=>{
        event.preventDefault();
        
        props.setAddNew(props =>props.concat({name:name}))
        
        alert(`${name}`)

            }


    return ( 
    <>

<h1 className='subject'>Youtube website</h1>
            
            
            <form className='searchForm' onSubmit={handelSubmit}>  

   <input className='searchInput'
    type='search'
    value={name}
    placeholder ='Search'
    onChange={e => {setName(e.target.value)}}></input>


   <input className='searchBtn' type='submit'></input>
            
            </form> 
    
    </> );
}
