///REDUCER  /////////////////////////
const initState={
    yout:[{
        name:'Nora'
    },{
        name
    }
],
    count:0
}

  const YoutReducer =(state=initState,{type,payload})=>{


    switch(type){

        case 'WATCH_LATER':
            return{
                ...state,
                yout:payload,
                count:state.count + payload   
            }

            default:
                return state
    }
    
}

export default YoutReducer;


//ACTION   /////////////////////////////////////////////////////////
export const watchLater=()=>{
    return{
        type:'WATCH_LATER',
        payload:1
    }


}
