///REDUCER  /////////////////////////

const initState={
    yout:[],
    count:0
}

  const YoutReducer =(state=initState,{type,payload})=>{


    switch(type){

        case 'WATCH_LATER':
            
            return{
                 
              
                yout:[...state.yout,payload],
                // count:state.count + 1 ,
               
            }

            default:
                return state
    }
    
}

export default YoutReducer;


//ACTION   /////////////////////////////////////////////////////////
export const watchLater=(data)=>{
    return{
        type:'WATCH_LATER',
        payload:data
    }

 
}

export const remove=(data)=>{
return{
    type:''
}
}
