///REDUCER  /////////////////////////
const initState={
    youtube:[],
    count:0
}

  const YoutReducer =(state=initState,{type,payload})=>{


    switch(type){

        case 'DIS_YOUT':
            return{
                ...state,
                youtube:payload
            }

            default:
                return state
    }
    
}

export default YoutReducer;
//ACTION   /////////////////////////////////////////////////////////
export const display=(youtube)=>{
    return{
        type:'DIS_YOUT',
        payload:youtube
    }


}
